const plantillaTuEquipoIds = [501, 304, 405, 503, 603, 314, 412, 616, 323, 419, 619];
const suplentesTuEquipoIds = [402, 310, 517, 721, 1120];
const plantillaRivalIds = [301, 404, 504, 604, 704, 316, 411, 514, 321, 519, 720];
const suplentesRivalIds = [602, 508, 318, 816, 1104];

const jugadoresAsignadosIds = new Set([
    ...plantillaTuEquipoIds, ...suplentesTuEquipoIds, 
    ...plantillaRivalIds, ...suplentesRivalIds
]);

export const JUGADORES_EQUIPO: Jugador[] = plantillaTuEquipoIds.map(id => TODOS_LOS_JUGADORES.find(j => j.id === id)!);
export const JUGADORES_SUPLENTES: Jugador[] = suplentesTuEquipoIds.map(id => TODOS_LOS_JUGADORES.find(j => j.id === id)!);
export const JUGADORES_RIVAL: Jugador[] = plantillaRivalIds.map(id => TODOS_LOS_JUGADORES.find(j => j.id === id)!);
export const JUGADORES_SUPLENTES_RIVAL: Jugador[] = suplentesRivalIds.map(id => TODOS_LOS_JUGADORES.find(j => j.id === id)!);

const poolDeJugadoresLibres: Jugador[] = TODOS_LOS_JUGADORES.filter(
    jugador => !jugadoresAsignadosIds.has(jugador.id)
);

export const JUGADORES_LIBRES: Jugador[] = poolDeJugadoresLibres
    .sort(() => 0.5 - Math.random())
    .slice(0, 20);


